css.value:
.header {
    font-size: 200%;
    font-weight: bold;
    margin: 0 0 5px;
    text-transform: uppercase;
    text-align: center;
    color: #73562D;
    text-shadow: 0px 0px 1px #000;
}


.author {
    font-size: 100%;
    text-align: center;
    color: #8c8e6f;
    text-shadow: 0px 0px 1px #8c8e6f;
}

.chap, .part {
    font-size: 150%;
    font-weight: bold;
    text-align: center;
    text-shadow: 0px 0px 1px #000;
}

.tho {
    font-style: italic;
    margin-top: -0.5em;
    font-size: 85%;
}

h4 {
    text-align: center;
    color: #666633;
    text-shadow: 0px 0px 1px #000;
}

p {
    text-indent: 20pt;
    line-height: 140%;
    font-size: 100%;
}

.ebook {
    color: #888;
    font-style: italic;
    font-size: 75%;
    text-align: right;
}

.lv-1 {
    font-size: 150%;
    font-weight: bold;
    line-height: 140%;
    text-indent: 20pt;
    margin-left: 0em;
}

.lv-2 {
    line-height: 120%;
    text-indent: 10pt;
    font-size: 75%;
    margin-left: 2em;
}

a {
    color: #666633;
}

.drop {
    font-size: 150%;
    font-weight: bold;
}
